import * as React from 'react';
import { Center, Box, Container } from 'native-base';
import { Dimensions, Platform } from 'react-native';
import { AppHeader } from '../../components/AppHeader';

interface IProps {
    navigation?: any
}

const Home: React.FC<IProps> = (props) => {
    const { width, height } = Dimensions.get('window');

    return <Container maxW={'100%'} bg={'gray.200'} h={height}>
        <AppHeader color={'black'} bg={'gray.200'} navigation={props.navigation} isDrawer={Platform.OS === 'web' ? true : false} />
        <Box bg={'white'} w={'94%'} maxW={'94%'} mx={3} mt={1} p={5}>
            This is a Box with Linear Gradient
        </Box>
    </Container>
}

export default Home;
