import * as React from 'react';
import { Center, Box, Container } from 'native-base';
import { Dimensions, Platform } from 'react-native';
import { AppHeader } from '../../components/AppHeader';

interface IProps {
    navigation?: any
}

const Home: React.FC<IProps> = (props) => {
    const { width, height } = Dimensions.get('window');

    return <Center w={width} bg={'primary.500'} h={height}>
        <Container w={width} bg={'primary.500'} h={height}>
            <AppHeader navigation={props.navigation} isDrawer={Platform.OS === 'web' ? true : false} />
            <Box >
                This is a Box with Linear Gradient
            </Box>
        </Container>
    </Center>
}

export default Home;
