import * as React from 'react';
import { Container, Box, Divider, VStack } from 'native-base';
import { Dimensions, Platform } from 'react-native';
import { AppHeader } from '../../components/AppHeader';

interface IProps {
    navigation?: any
}

const Home: React.FC<IProps> = (props) => {
    const { width,height } = Dimensions.get('window');

    return <Container bg={'blue.50'} w={width} h={height}>
        <AppHeader navigation={props.navigation} isDrawer={Platform.OS === 'web' ? true : false} />
        <Box bg={{
            linearGradient: {
                colors: ["lightBlue.300", "violet.800"],
                start: [0, 0],
                end: [1, 0]
            }
        }} p="12" rounded="xl" _text={{
            fontSize: "md",
            fontWeight: "medium",
            color: "warmGray.50",
            textAlign: "center"
        }} w={width}>
            This is a Box with Linear Gradient
        </Box>
    </Container>
}

export default Home;
