import * as React from "react";
import { HStack, IconButton, Icon, Text } from "native-base"
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import { Dimensions } from "react-native";
import * as RootNavigator from "../navigation/RootNavigator";

interface IProps {
    isDrawer?: boolean
}

export const AppHeader: React.FC<IProps> = (props) => {
    const { width } = Dimensions.get('window');

    return <HStack bg="#6200ee" px="1" py="3" justifyContent="space-between" alignItems="center" w={width}>
        <HStack alignItems="center">
            {props?.isDrawer &&
                <IconButton icon={<Icon as={MaterialIcons} size="sm" name="menu" color="white" />} onPress={RootNavigator.toggleDrawer} />
            }
            <Text color="white" fontSize="20" fontWeight="bold">
                Home
            </Text>
        </HStack>
        <HStack>
            <IconButton icon={<Icon as={MaterialIcons} name="favorite" size="sm" color="white" />} />
            <IconButton icon={<Icon as={MaterialIcons} name="search" size="sm" color="white" />} />
            <IconButton icon={<Icon as={MaterialIcons} name="more-vert" size="sm" color="white" />} />
        </HStack>
    </HStack>
}
