import * as React from 'react';
import { Box, Center, FormControl, Heading, HStack, Input, Button, Link, Text, VStack } from 'native-base';
import { StyleSheet } from 'react-native';

interface IProps {

}

const Login: React.FC<IProps> = () => {

    return <Center w="100%" backgroundColor={'white'} flex={1}>
        <Box safeArea p="2" py="8" w="90%" maxW="290" bg={'primary.500'} borderRadius='md'>
            <Box alignItems='center'>
                <Heading size="lg" fontWeight="600" color="coolGray.800"
                    _dark={{ color: "warmGray.50" }}>
                    Sport Buddy
                </Heading>
                <Heading mt="1" _dark={{
                    color: "warmGray.200"
                }} color="coolGray.600" fontWeight="medium" size="xs">
                    Login
                </Heading>
            </Box>

            <VStack space={3} mt="5">
                <FormControl>
                    <FormControl.Label>Email ID</FormControl.Label>
                    <Input />
                </FormControl>
                <FormControl>
                    <FormControl.Label>Password</FormControl.Label>
                    <Input type="password" />
                    <Link _text={{
                        fontSize: "xs",
                        fontWeight: "500",
                        color: "indigo.500"
                    }} alignSelf="flex-end" mt="1">
                        Forget Password?
                    </Link>
                </FormControl>
                <Button mt="2" colorScheme="indigo">
                    Sign in
                </Button>
                {/* <HStack mt="6" justifyContent="center">
                    <Text fontSize="sm" color="coolGray.600" _dark={{
                        color: "warmGray.200"
                    }}>
                        I'm a new user.{" "}
                    </Text>
                    <Link _text={{
                        color: "indigo.500",
                        fontWeight: "medium",
                        fontSize: "sm"
                    }} href="#">
                        Sign Up
                    </Link>
                </HStack> */}
            </VStack>
        </Box>
    </Center>;
};

export default Login;
