import { observable, action, makeObservable } from "mobx";
import HttpWrapper from "../utils/HttpClient";

export default class CommonStore {
    @observable isLoading: boolean = false;
    @observable email: string = '';
    @observable password: string = '';

    constructor() {
        makeObservable(this);
    }

    @action async login(data:any){
        console.log(data)
        var postdata = {
              "email": data.email,
              "password":data.password
        }
        this.setLoaderStatus(true);
        await HttpWrapper().postResponse('users/login', postdata).then(()=>{
          alert('Login Successful')}).catch(()=>{
              alert('error occurs in login')
        })
      }

      @action setLoaderStatus = (status: boolean) => {
        this.isLoading = status
     }

}
