import * as React from 'react';
import { Center, Box, Container, HStack, VStack, Text, View } from 'native-base';
import { Dimensions, Platform } from 'react-native';
import { AppHeader } from '../../components/AppHeader';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';

interface IProps {
    navigation?: any
}

const homeDatas: any = [{ icon: '', description: 'My Calendar' }, { icon: '', description: 'Create Activity' },
{ icon: '', description: 'Favourite Venues' }, { icon: '', description: 'Groups' }, { icon: '', description: 'Offers' }];

const Home: React.FC<IProps> = (props) => {
    const { width, height } = Dimensions.get('window');

    return <Container maxW={'100%'} bg={'gray.200'} h={height}>
        <AppHeader color={'black'} bg={'gray.200'} navigation={props.navigation} isDrawer={Platform.OS === 'web' ? true : false} />
        <Box bg={'white'} w={'94%'} maxW={'94%'} mx={3} mt={2} p={3} borderRadius={5}>
            This is a Box with Linear Gradient
        </Box>
        <Box bg={'white'} w={'94%'} maxW={'94%'} mx={3} mt={5} p={3} borderRadius={5}>
            <HStack space={3} justifyContent={'space-around'}>
                <VStack space={1}>
                    <View h={20} backgroundColor={'green.400'} borderRadius={20}
                        justifyContent='center' alignItems='center'>
                        <MaterialCommunityIcons size={18} color={'white'} name="calendar" />
                    </View>
                    <Text>aa</Text>
                </VStack>
                <VStack space={1}>
                    <Text>aa</Text>
                    <Text>aa</Text>
                </VStack>
                <VStack space={1}>
                    <Text>aa</Text>
                    <Text>aa</Text>
                </VStack>
            </HStack>
        </Box>
    </Container>
}

export default Home;
